import React, { useState, useEffect } from "react";
import { MapContainer, TileLayer, Marker, useMap } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";
import "leaflet-routing-machine";
import "./style.css"; // Add your styles here

const DEFAULT_LOCATION = [10.9027, 76.9006]; // Ettimadai default location

const Routing = ({ userLocation, destination, setDirections }) => {
  const map = useMap();
  const routingRef = React.useRef(null); // Store routing instance

  useEffect(() => {
    if (!userLocation || !destination) return;

    // Remove previous route if exists
    if (routingRef.current) {
      map.removeControl(routingRef.current);
    }

    // Create new route
    routingRef.current = L.Routing.control({
      waypoints: [
        L.latLng(userLocation[0], userLocation[1]),
        L.latLng(destination[0], destination[1]),
      ],
      routeWhileDragging: true,
      show: false,
      createMarker: () => null, // No default markers
      lineOptions: { styles: [{ color: "red", weight: 5 }] },
    }).addTo(map);

    // Capture route directions
    routingRef.current.on("routesfound", function (e) {
      const steps = e.routes[0].instructions.map((step) => step.text);
      setDirections(steps);
    });

    return () => {
      if (routingRef.current) {
        map.removeControl(routingRef.current);
        routingRef.current = null;
      }
    };
  }, [userLocation, destination, map, setDirections]);

  return null;
};

const App = () => {
  const [userLocation, setUserLocation] = useState(DEFAULT_LOCATION);
  const [destination, setDestination] = useState(null);
  const [directions, setDirections] = useState([]);

  const handleSearch = () => {
    // Hardcoded Destination: Coimbatore Airport (Modify as needed)
    setDestination([11.029, 77.044]); 
  };

  return (
    <div className="app-container">
      <h1>Google Maps Clone (Using GMaps & ORS)</h1>
      <div className="search-container">
        <input type="text" placeholder="Enter destination..." />
        <button onClick={handleSearch}>Search Route</button>
      </div>

      <MapContainer center={userLocation} zoom={12} className="map">
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
        <Marker position={userLocation} />
        {destination && <Marker position={destination} />}
        {destination && (
          <Routing
            userLocation={userLocation}
            destination={destination}
            setDirections={setDirections}
          />
        )}
      </MapContainer>

      {/* Direction Box Below the Map */}
      <div className="directions-container">
        <h2>Directions</h2>
        <ul>
          {directions.map((step, index) => (
            <li key={index}>{step}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default App;
