import React, { useEffect, useState } from "react";
import L from "leaflet";
import "leaflet-routing-machine";
import { MapContainer, TileLayer, Marker, useMap } from "react-leaflet";
import axios from "axios";

const userLocation = [10.9027, 76.9006]; // Example: Ettimadai Coordinates

const Routing = ({ destinationCoords }) => {
  const map = useMap();

  useEffect(() => {
    if (!map || !destinationCoords) return;

    // Clear previous layers before adding a new route
    map.eachLayer((layer) => {
      if (layer.options && layer.options.className === "route-layer") {
        map.removeLayer(layer);
      }
    });

    const routingControl = L.Routing.control({
      waypoints: [
        L.latLng(userLocation[0], userLocation[1]),  // User's location
        L.latLng(destinationCoords[0], destinationCoords[1]),  // Destination
      ],
      createMarker: () => null, // Removes default markers
      routeWhileDragging: true,
      addWaypoints: false, // Disables waypoint addition
      draggableWaypoints: false, // Prevents dragging points
      showAlternatives: false, // Hides alternative routes
      fitSelectedRoutes: true,
      lineOptions: {
        styles: [{ color: "red", weight: 4 }], // Customize route color
        className: "route-layer",
      },
      itinerary: false, // **This disables the directions popup**
    }).addTo(map);

    return () => map.removeControl(routingControl);
  }, [map, destinationCoords]);

  return null;
};

const App = () => {
  const [destination, setDestination] = useState("");
  const [destinationCoords, setDestinationCoords] = useState(null);

  const fetchCoordinates = async () => {
    try {
      const response = await axios.get(
        `https://nominatim.openstreetmap.org/search?format=json&q=${destination}`
      );

      if (response.data.length > 0) {
        const { lat, lon } = response.data[0];
        setDestinationCoords([parseFloat(lat), parseFloat(lon)]);
      } else {
        alert("Location not found. Please try again.");
      }
    } catch (error) {
      console.error("Error fetching location:", error);
    }
  };

  return (
    <div style={{ textAlign: "center", padding: "10px" }}>
      <h1>Google Maps Clone (Using GMaps & ORS)</h1>
      <input
        type="text"
        placeholder="Enter destination..."
        value={destination}
        onChange={(e) => setDestination(e.target.value)}
        style={{
          padding: "8px",
          width: "300px",
          marginRight: "10px",
        }}
      />
      <button
        onClick={fetchCoordinates}
        style={{
          padding: "8px 15px",
          backgroundColor: "#007bff",
          color: "#fff",
          border: "none",
          cursor: "pointer",
        }}
      >
        Search Route
      </button>

      <MapContainer
        center={userLocation}
        zoom={14}
        style={{
          height: "500px",
          width: "90%",
          margin: "20px auto",
          border: "2px solid black",
        }}
      >
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
        <Marker position={userLocation} />
        {destinationCoords && <Marker position={destinationCoords} />}
        {destinationCoords && <Routing destinationCoords={destinationCoords} />}
      </MapContainer>
    </div>
  );
};

export default App;
