import React, { useState } from "react";
import { MapContainer, TileLayer, Marker, Polyline } from "react-leaflet";
import axios from "axios";
import "leaflet/dist/leaflet.css";

const TOMTOM_API_KEY = "YOUR_TOMTOM_API_KEY"; // Replace with your TomTom API Key

const predefinedLocations = {
    "Ettimadai": [10.9027, 76.9006],
    "Coimbatore Airport": [11.029, 77.0403],
    "Brookefields Mall": [11.0144, 76.9802],
    "Gandhipuram Bus Stand": [11.0176, 76.9725]
};

const App = () => {
    const [source] = useState(predefinedLocations["Ettimadai"]);
    const [destination, setDestination] = useState("");
    const [route, setRoute] = useState([]);
    const [error, setError] = useState("");

    const getRoute = async (destinationCoords) => {
        try {
            const response = await axios.get(
                `https://api.tomtom.com/routing/1/calculateRoute/${source[0]},${source[1]}:${destinationCoords[0]},${destinationCoords[1]}/json?key=${TOMTOM_API_KEY}&routeType=fastest`
            );

            if (response.data.routes && response.data.routes.length > 0) {
                const routePoints = response.data.routes[0].legs[0].points.map(point => [
                    point.latitude,
                    point.longitude
                ]);

                setRoute(routePoints);
            } else {
                setError("No route found.");
            }
        } catch (error) {
            console.error("Error fetching route:", error);
            setError("Failed to fetch route.");
        }
    };

    const handleSearch = () => {
        setError(""); // Clear previous errors
        if (!destination || !predefinedLocations[destination]) {
            setError("Invalid destination. Choose a predefined location.");
            return;
        }

        const destinationCoords = predefinedLocations[destination];
        getRoute(destinationCoords);
    };

    return (
        <div style={{ textAlign: "center", padding: "20px" }}>
            <h2>Smart City Navigation (Leaflet + TomTom Routing)</h2>

            <select value={destination} onChange={(e) => setDestination(e.target.value)} style={{ padding: "8px", marginRight: "8px" }}>
                <option value="">Select Destination</option>
                {Object.keys(predefinedLocations).map((place) => (
                    <option key={place} value={place}>
                        {place}
                    </option>
                ))}
            </select>

            <button onClick={handleSearch} style={{ padding: "8px", cursor: "pointer" }}>
                Search Route
            </button>

            {error && <p style={{ color: "red" }}>{error}</p>}

            <MapContainer center={source} zoom={14} style={{ height: "500px", width: "100%", marginTop: "20px" }}>
                <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
                <Marker position={source} />
                {route.length > 0 && <Polyline positions={route} color="red" />}
            </MapContainer>
        </div>
    );
};

export default App;
