import React, { useState, useEffect } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import L from "leaflet"; // Import Leaflet for custom icons
import io from "socket.io-client";
import "leaflet/dist/leaflet.css";

const socket = io("http://127.0.0.1:5000");

// Custom Icons for Leader and Normal Users
const defaultIcon = new L.Icon({
  iconUrl: "https://cdn-icons-png.flaticon.com/512/149/149059.png", // Regular marker icon
  iconSize: [30, 30], // Size of the icon
  iconAnchor: [15, 30], // Positioning of the icon
  popupAnchor: [0, -30],
});

const leaderIcon = new L.Icon({
  iconUrl: "https://cdn-icons-png.flaticon.com/512/1828/1828640.png", // Special icon for leader
  iconSize: [35, 35], // Larger size for the leader
  iconAnchor: [17, 35],
  popupAnchor: [0, -35],
});

const App = () => {
  const [userLocation, setUserLocation] = useState(null);
  const [otherUsers, setOtherUsers] = useState({});
  const [leader, setLeader] = useState(null);

  // Get user location on load
  useEffect(() => {
    navigator.geolocation.getCurrentPosition((position) => {
      const { latitude, longitude } = position.coords;
      setUserLocation({ lat: latitude, lng: longitude });

      // Send location to server
      socket.emit("update_location", { user_id: socket.id, lat: latitude, lng: longitude });
    });

    // Listen for location updates
    socket.on("location_update", (locations) => {
      setOtherUsers(locations);
    });

    // Listen for leader updates
    socket.on("leader_update", (data) => {
      setLeader(data.leader);
    });

    return () => socket.disconnect();
  }, []);

  const triggerElection = () => {
    console.log("Triggering new election...");
    socket.emit("start_election"); // Manually start an election
  };

  return (
    <div>
      <h2>Google Maps Clone (Leaflet.js + Flask)</h2>
      <p>Leader: {leader ? leader : "None"}</p>
      <button onClick={triggerElection}>Trigger Election</button>

      <MapContainer center={[51.505, -0.09]} zoom={13} style={{ height: "80vh", width: "100%" }}>
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
        
        {userLocation && (
          <Marker
            position={[userLocation.lat, userLocation.lng]}
            icon={leader === socket.id ? leaderIcon : defaultIcon} // Highlight current user if they are leader
          >
            <Popup>You are here! {leader === socket.id ? "(Leader)" : ""}</Popup>
          </Marker>
        )}

        {Object.entries(otherUsers).map(([userId, location]) => (
          <Marker
            key={userId}
            position={[location.lat, location.lng]}
            icon={leader === userId ? leaderIcon : defaultIcon} // Highlight leader
          >
            <Popup>User {userId} {leader === userId ? "(Leader)" : ""}</Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
};

export default App;
