import React, { useEffect, useState } from "react";
import axios from "axios";
import { GoogleMap, LoadScript, Marker, DirectionsService, DirectionsRenderer } from "@react-google-maps/api";

const mapContainerStyle = { width: "100vw", height: "100vh" };
const ettimadaiCenter = { lat: 10.9053, lng: 76.9006 };

function App() {
    const [locations, setLocations] = useState([]);
    const [destination, setDestination] = useState("");
    const [directions, setDirections] = useState(null);
    const [userLocation, setUserLocation] = useState(null);

    // Fetch locations from backend
    useEffect(() => {
        axios.get("http://localhost:5000/locations")
            .then(response => setLocations(response.data))
            .catch(error => console.error("Error fetching locations:", error));
    }, []);

    // Get user location
    useEffect(() => {
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(position => {
                setUserLocation({
                    lat: position.coords.latitude,
                    lng: position.coords.longitude
                });
            });
        }
    }, []);

    // Handle search and find route
    const handleSearch = () => {
        if (!destination || !userLocation) return;

        const directionsService = new window.google.maps.DirectionsService();
        directionsService.route(
            {
                origin: userLocation,
                destination: destination,
                travelMode: window.google.maps.TravelMode.DRIVING
            },
            (result, status) => {
                if (status === window.google.maps.DirectionsStatus.OK) {
                    setDirections(result);
                } else {
                    console.error("Error fetching directions:", status);
                }
            }
        );
    };

    return (
        <LoadScript googleMapsApiKey="YOUR_GOOGLE_MAPS_API_KEY">
            <div style={{ padding: "10px", display: "flex", justifyContent: "center" }}>
                <input
                    type="text"
                    placeholder="Enter destination"
                    value={destination}
                    onChange={(e) => setDestination(e.target.value)}
                    style={{ padding: "5px", marginRight: "10px", width: "250px" }}
                />
                <button onClick={handleSearch}>Search Route</button>
            </div>
            <GoogleMap mapContainerStyle={mapContainerStyle} center={ettimadaiCenter} zoom={12}>
                {userLocation && <Marker position={userLocation} label="You" />}
                {locations.map((loc, index) => (
                    <Marker key={index} position={{ lat: loc.lat, lng: loc.lng }} />
                ))}
                {directions && <DirectionsRenderer directions={directions} />}
            </GoogleMap>
        </LoadScript>
    );
}

export default App;
