import React, { useEffect, useState, useRef } from "react";
import { MapContainer, TileLayer, Marker, useMap } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";
import "leaflet-routing-machine";
import "./styles.css"; // Import the CSS file

const DEFAULT_CENTER = [10.9027, 76.9006]; // Ettimadai, Coimbatore

// ðŸŸ¢ Custom Icon for User & Destination Markers
const customIcon = new L.Icon({
  iconUrl: require("./marker.png"), // Replace with your marker image
  iconSize: [40, 40], // Adjust size if needed
  iconAnchor: [20, 40],
  popupAnchor: [0, -40],
});

const Routing = ({ userLocation, destination, setDirections, setShowPopup }) => {
  const map = useMap();
  const routingControlRef = useRef(null);

  useEffect(() => {
    if (!userLocation || !destination || !map) return;

    if (routingControlRef.current) {
      map.removeControl(routingControlRef.current);
      routingControlRef.current = null;
      setDirections([]);
    }

    const routingControl = L.Routing.control({
      waypoints: [
        L.latLng(userLocation[0], userLocation[1]),
        L.latLng(destination[0], destination[1]),
      ],
      routeWhileDragging: true,
      createMarker: () => null,
      show: false,
      addWaypoints: false,
      draggableWaypoints: false,
      fitSelectedRoutes: true,
      router: L.Routing.osrmv1({
        profile: "driving",
      }),
    }).addTo(map);

    routingControlRef.current = routingControl;

    routingControl.on("routesfound", function (e) {
      const routes = e.routes[0].instructions.map((step) => step.text);
      setDirections(routes);
      setShowPopup(true); // Show popup when route is found
    });

    return () => {
      if (routingControlRef.current) {
        map.removeControl(routingControlRef.current);
        routingControlRef.current = null;
      }
    };
  }, [userLocation, destination, map, setDirections, setShowPopup]);

  return null;
};

const App = () => {
  const [userLocation, setUserLocation] = useState(null);
  const [destination, setDestination] = useState(null);
  const [searchInput, setSearchInput] = useState("");
  const [directions, setDirections] = useState([]);
  const [showPopup, setShowPopup] = useState(false); // Popup visibility state

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      (position) => {
        setUserLocation([position.coords.latitude, position.coords.longitude]);
      },
      () => {
        alert("Could not get your location.");
      }
    );
  }, []);

  const handleSearch = async () => {
    if (!searchInput) return alert("Please enter a destination");

    try {
      const response = await fetch(
        `https://nominatim.openstreetmap.org/search?format=json&q=${searchInput}`
      );
      const data = await response.json();

      if (data.length > 0) {
        setDestination([parseFloat(data[0].lat), parseFloat(data[0].lon)]);
      } else {
        alert("Location not found. Try a different place.");
      }
    } catch (error) {
      alert("Error fetching location.");
    }
  };

  return (
    <div className="container">
      <MapContainer
        center={DEFAULT_CENTER}
        zoom={12}
        className="full-screen-map"
      >
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
        {userLocation && <Marker position={userLocation} icon={customIcon} />}
        {destination && <Marker position={destination} icon={customIcon} />}
        {userLocation && destination && (
          <Routing
            key={destination ? `${destination[0]}-${destination[1]}` : "initial"}
            userLocation={userLocation}
            destination={destination}
            setDirections={setDirections}
            setShowPopup={setShowPopup}
          />
        )}
      </MapContainer>

      <div className="search-container">
        <input
          type="text"
          placeholder="Enter destination"
          value={searchInput}
          onChange={(e) => setSearchInput(e.target.value)}
        />
        <button onClick={handleSearch}>Search Route</button>
      </div>

      {/* ðŸš€ Popup appears in the right corner when a destination is searched */}
      {showPopup && (
        <div className="directions-popup">
          <button className="close-button" onClick={() => setShowPopup(false)}>Ã—</button>
          <h2>Directions</h2>
          {directions.length === 0 ? (
            <p>Fetching directions...</p>
          ) : (
            <ol>
              {directions.map((step, index) => (
                <li key={index}>{step}</li>
              ))}
            </ol>
          )}
        </div>
      )}
    </div>
  );
};

export default App;
