import React, { useState, useEffect } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import io from "socket.io-client";
import "leaflet/dist/leaflet.css";
import L from "leaflet"; // ✅ Ensure this is at the top

const socket = io("http://127.0.0.1:5000");

// Define marker icons
const defaultIcon = new L.Icon({
  iconUrl: "https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon.png",
  iconSize: [25, 41],
});

const customLeaderIcon = new L.Icon({
  iconUrl: "https://upload.wikimedia.org/wikipedia/commons/e/ec/RedDot.svg", // Red dot for leader
  iconSize: [20, 20],
});

const App = () => {
  const [userLocation, setUserLocation] = useState(null);
  const [otherUsers, setOtherUsers] = useState({});
  const [leader, setLeader] = useState(null);

  // Get user location on load
  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      (position) => {
        const { latitude, longitude } = position.coords;
        setUserLocation({ lat: latitude, lng: longitude });

        // Send location to server
        socket.emit("update_location", { user_id: socket.id, lat: latitude, lng: longitude });
      },
      (error) => {
        console.error("Error getting location:", error);
      }
    );

    // Listen for location updates
    socket.on("location_update", (locations) => {
      setOtherUsers(locations);
    });

    // Listen for leader updates
    socket.on("leader_update", (data) => {
      setLeader(data.leader);
    });

    return () => socket.disconnect();
  }, []);

  return (
    <div>
      <h2>Google Maps Clone (Leaflet.js + Flask)</h2>
      <h3>Leader: {leader ? `User ${leader}` : "None"}</h3>

      <MapContainer center={[51.505, -0.09]} zoom={13} style={{ height: "80vh", width: "100%" }}>
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

        {/* ✅ Only render the marker when userLocation is available */}
        {userLocation && (
          <Marker position={[userLocation.lat, userLocation.lng]} icon={defaultIcon}>
            <Popup>You are here!</Popup>
          </Marker>
        )}

        {/* ✅ Only render other users if they have valid locations */}
        {Object.entries(otherUsers).map(([userId, location]) =>
          location.lat && location.lng ? (
            <Marker
              key={userId}
              position={[location.lat, location.lng]}
              icon={userId === leader ? customLeaderIcon : defaultIcon}
            >
              <Popup>
                User {userId} {userId === leader ? "(Leader)" : ""}
              </Popup>
            </Marker>
          ) : null
        )}
      </MapContainer>
    </div>
  );
};

export default App;
