import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, useMap } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";
import "leaflet-routing-machine";

import "./styles.css";

const Routing = ({ userLocation, destination, setDirections }) => {
  const map = useMap();

  useEffect(() => {
    if (!userLocation || !destination) return;

    const routingControl = L.Routing.control({
      waypoints: [
        L.latLng(userLocation[0], userLocation[1]),
        L.latLng(destination[0], destination[1]),
      ],
      routeWhileDragging: true,
      createMarker: () => null, // ❌ Prevents adding markers
      show: false, // ❌ Hides default routing UI
      addWaypoints: false,
      fitSelectedRoutes: true,
      showAlternatives: false,
    }).addTo(map);

    routingControl.on("routesfound", function (e) {
      const route = e.routes[0];
      const steps = route.instructions.map((step) => step.text);
      setDirections(steps); // ✅ Stores directions in state
    });

    return () => map.removeControl(routingControl);
  }, [userLocation, destination, map, setDirections]);

  return null;
};

const App = () => {
  const [userLocation, setUserLocation] = useState(null);
  const [destination, setDestination] = useState(null);
  const [searchInput, setSearchInput] = useState("");
  const [directions, setDirections] = useState([]); // ✅ Stores the directions

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      (position) => {
        setUserLocation([position.coords.latitude, position.coords.longitude]);
      },
      () => {
        alert("Could not get your location.");
      }
    );
  }, []);

  const handleSearch = async () => {
    if (!searchInput) return alert("Please enter a destination");

    try {
      const response = await fetch(
        `https://nominatim.openstreetmap.org/search?format=json&q=${searchInput}`
      );
      const data = await response.json();

      if (data.length > 0) {
        setDestination([data[0].lat, data[0].lon]);
      } else {
        alert("Location not found. Try a different place.");
      }
    } catch (error) {
      alert("Error fetching location.");
    }
  };

  return (
    <div className="app-container">
      <h1>Google Maps Clone (Using GMaps & ORS)</h1>

      <div className="search-container">
        <input
          type="text"
          placeholder="Enter destination"
          value={searchInput}
          onChange={(e) => setSearchInput(e.target.value)}
        />
        <button onClick={handleSearch}>Search Route</button>
      </div>

      <MapContainer
        center={[10.9027, 76.9006]}
        zoom={12}
        style={{ height: "500px", width: "100%" }}
      >
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
        {userLocation && <Marker position={userLocation}></Marker>}
        {destination && <Marker position={destination}></Marker>}
        {userLocation && destination && (
          <Routing
            userLocation={userLocation}
            destination={destination}
            setDirections={setDirections} // ✅ Store directions in state
          />
        )}
      </MapContainer>

      {/* ✅ Directions Panel at Bottom */}
      <div className="directions-container">
        <h2>Directions</h2>
        {directions.length > 0 ? (
          directions.map((step, index) => <p key={index}>{step}</p>)
        ) : (
          <p>No directions available.</p>
        )}
      </div>
    </div>
  );
};

export default App;
