import React, { useState, useEffect } from "react";
import { MapContainer, TileLayer, Polyline, Marker } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";

const API_KEY = "5b3ce3597851110001cf6248b9aa70610e5d4dcaae9d9ef7ea9871d1"; // Replace with your API Key

const App = () => {
  const [userLocation, setUserLocation] = useState(null);
  const [destination, setDestination] = useState("");
  const [route, setRoute] = useState([]);
  const [error, setError] = useState("");

  // Get User's Current Location
  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      (position) => {
        setUserLocation([position.coords.latitude, position.coords.longitude]);
      },
      (error) => {
        console.error("Error getting location:", error);
        setError("Location access denied. Enter a destination manually.");
      }
    );
  }, []);

  // Function to Fetch Coordinates of Destination
  const getCoordinates = async (location) => {
    try {
      const response = await fetch(
        `https://api.openrouteservice.org/geocode/search?api_key=${API_KEY}&text=${encodeURIComponent(location)}`
      );

      if (!response.ok) throw new Error(`Geocoding API Error: ${response.statusText}`);

      const data = await response.json();
      if (!data.features.length) {
        alert("Invalid location. Try again.");
        return null;
      }

      return data.features[0].geometry.coordinates.reverse();
    } catch (error) {
      console.error("Error fetching coordinates:", error);
      alert("Failed to fetch coordinates.");
      return null;
    }
  };

  // Function to Fetch Route
  const fetchRoute = async (start, end) => {
    try {
      const response = await fetch(
        "https://api.openrouteservice.org/v2/directions/driving-car/geojson",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            "Authorization": API_KEY,
          },
          body: JSON.stringify({ coordinates: [start, end] }),
        }
      );

      if (!response.ok) throw new Error(`Routing API Error: ${response.statusText}`);

      const data = await response.json();
      if (!data.features.length) {
        alert("No route found.");
        return [];
      }

      return data.features[0].geometry.coordinates.map((coord) => [coord[1], coord[0]]);
    } catch (error) {
      console.error("Error fetching route:", error);
      alert("Failed to fetch route.");
      return [];
    }
  };

  // Handle Search Button Click
  const handleSearch = async () => {
    if (!userLocation) {
      alert("Waiting for location...");
      return;
    }

    const destinationCoords = await getCoordinates(destination);
    if (!destinationCoords) return;

    const routeData = await fetchRoute(userLocation, destinationCoords);
    setRoute(routeData);
  };

  return (
    <div className="app-container">
      <h1>Google Maps Clone (Using OpenRouteService)</h1>

      <div className="search-container">
        <input
          type="text"
          placeholder="Enter destination..."
          value={destination}
          onChange={(e) => setDestination(e.target.value)}
        />
        <button onClick={handleSearch}>Search Route</button>
      </div>

      {error && <p className="error">{error}</p>}

      {userLocation && (
        <MapContainer center={userLocation} zoom={14} className="map-container">
          <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

          {/* User's Location Marker */}
          <Marker position={userLocation} icon={L.icon({ iconUrl: "https://leafletjs.com/examples/custom-icons/leaf-red.png", iconSize: [25, 41] })} />

          {/* Destination Marker */}
          {route.length > 0 && <Marker position={route[route.length - 1]} />}

          {/* Route Line (Red) */}
          {route.length > 0 && <Polyline positions={route} color="red" />}
        </MapContainer>
      )}
    </div>
  );
};

export default App;
